import React, { useEffect, useState } from 'react'
import Shimmer from './Shimmer';
import { useParams } from 'react-router';
import { resturantMenu } from './urlhub';

const Resturanmenu = () => {
    const [menu,setmenu]=useState(null);
    const{resid}=useParams();

    useEffect(()=>{
        fetchmenu();
    },[])

    async function fetchmenu() {
        const data=await fetch("https://www.swiggy.com/dapi/menu/pl?page-type=REGULAR_MENU&complete-menu=true&lat=12.9352403&lng=77.624532&restaurantId="+re);
        const json=await data.json();
        setmenu(json.data)
    }
    
   if(menu===null)return <Shimmer/>;

   const{name,avgRating,totalRatingsString,costForTwoMessage,cuisines}=menu?.cards?.[2]?.card?.card?.info;
   const{itemCards}=menu?.cards[4]?.groupedCard?.cardGroupMap?.REGULAR?.cards[2]?.card?.card;


  return (
    <div>
      <h1>{name}</h1>
      <h2>{avgRating}</h2>
      <h2>{totalRatingsString}</h2>
      <h2>{costForTwoMessage}</h2>
      <h3>{cuisines.join(",")}</h3>
      <h1>Menu</h1>
      <ul>
        {itemCards.map((item)=>(
            <li key={item.card.info.id}>
                {item.card.info.name}  {"Rs-"}
                {item.card.info.defaultPrice/100 || item.card.info.price/100}
            </li>
        ))}
      </ul>
    </div> 
  )
}

export default Resturanmenu
