import React, { useEffect, useState } from 'react'
import Shimmer from './Shimmer';


const Resturanmenu = () => {
    const [menu,setmenu]=useState(null);
    useEffect(()=>{
        fetchmenu();
    },[])

    async function fetchmenu() {
        const data=await fetch("https://www.swiggy.com/dapi/menu/pl?page-type=REGULAR_MENU&complete-menu=true&lat=12.9352403&lng=77.624532&restaurantId=275&catalog_qa=undefined&submitAction=ENTER");
        const json=await data.json();
        console.log(json);
        setmenu(json.data)
    }
   if(menu===Null)return <Shimmer/>;
   
   const{name,avgRating,totalRatingsString,costForTwoMessage,cuisines}=menu?.cards?.[2]?.card?.card?.info;

  return (
    <div>
      <h1>{name}</h1>
      <h2>{avgRating}</h2>
      <h2>{totalRatingsString}</h2>
      <h2>{costForTwoMessage}</h2>
      <h3>{cuisines.join(",")}</h3>
      <h1>Menu</h1>
      <ul>

      </ul>
    </div> 
  )
}

export default Resturanmenu
