import React, { useState } from 'react';
import { LOGO_URL } from './urlhub';
import { Link } from 'react-router-dom'; // Correct import
import { useSelector } from 'react-redux';

const Header = () => {
  const [logbtn, setbtn] = useState("Login");
  const cartitems = useSelector((store) => store.cart.items);

  return (
    <div className="flex justify-between items-center shadow-lg bg-pink-800 text-white px-6 py-4">
      {/* Logo */}
      <div className="flex items-center space-x-4">
        <img className="w-20 h-20" src={LOGO_URL} alt="logo" />
        <h1 className="text-4xl font-extrabold text-white tracking-wide">
          Food <span className="text-yellow-300">Mania</span>
        </h1>
      </div>

      {/* Navigation Links */}
      <ul className="flex space-x-6 items-center">
        <li className="font-bold hover:text-yellow-300 transition duration-300">
          <Link to="/">Home</Link>
        </li>
        <li className="font-bold hover:text-yellow-300 transition duration-300">
          <Link to="/About">About</Link>
        </li>
      </ul>

      {/* Cart & Sign In */}
      <div className="flex items-center space-x-4">
        <li className="font-semibold text-sm flex items-center">
          <span className="mr-1">ðŸ›’</span> ({cartitems.length})
        </li>
        <li className="font-semibold hover:text-yellow-300 transition duration-300">
          <Link to="/sign-in">{logbtn}</Link>
        </li>
        <li className="relative">
          <Link to="/Cart">
            <img className="w-8 h-6" src="grocery-store.png" alt="Cart" />
          </Link>
        </li>
      </div>
    </div>
  );
};

export default Header;
