import React from 'react'
import {RESTAURANT_IMG} from './urlhub'
import { Link } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { additem } from './CartSlice'

const Listitem = ({items}) => {
    // console.log(items);
    const dispatch=useDispatch();
    const clickhandler=(items)=>{
       dispatch(additem(item))
    }
  return (
    <div>
        {items.map((item)=>(
            <div key={item.card.info.id} className='p-2 m-2 border-b-2 border-gray-200 flex'>
                <div className='py-2 w-9/12'>
                    <span className='font-bold text-lg'>{item.card.info.name}</span>
                    <br></br>
                    <span className='font-semibold '>â‚¹{item.card.info.defaultPrice/100 ||item.card.info.price/100 }</span>
                    <p className='text-xs'>{item.card.info.description}</p>
                </div>
                <div className='w-3/12'>
                <div className='absolute'>
                    <button onClick={()=>clickhandler(item)} className='p-2 shadow-lg m-auto rounded-lg bg-green-400 text-white relative left-20 bottom-3 w-20 '>
                        ADD +</button>
                </div>
                    <img src={RESTAURANT_IMG+item.card.info.imageId} className='w-full'/>
                </div>
            </div>
        ))}
    </div>
  )
}

export default Listitem
