import { RESTAURANT_IMG } from "./urlhub";

const Cardresto = (props) => {
  const { Data } = props;

  const {
    
    cloudinaryImageId,
    name,
    cuisines,
    avgRating,
    costForTwo,
    sla,
  } = Data?.info;

  return (
    <div className="w-72 h-96 p-2 shadow-xl bg-slate-200 bg-opacity-75 cursor-pointer rounded-lg transition-transform duration-300 hover:scale-110 hover:bg-slate-300">
      <img
        className="w-full h-32 object-cover rounded-xl mb-4"
        src={RESTAURANT_IMG + cloudinaryImageId}
        alt="Biryani"
      />
      <div className="p-2 ">
        <h1 className="text-lg font-bold mb-2">{name}</h1>
        <h4 className="text-sm mb-2 font-semibold">{cuisines.join(', ')}</h4>
        <p className="mb-2 font-semibold">⭐ {avgRating} stars</p>
        <p className="flex justify-center font-semibold">{sla ?.slaString} minutes</p>
        <h4>₹{costForTwo} </h4>
      </div>
    </div>
  );
};

export default Cardresto
