import { useState ,useEffect } from 'react';
import Cardresto from './Cardresto'; 
import Shimmer from './Shimmer';
import { Link } from 'react-router';
import useActivestatus from './custom hooks/useActivestatus';


const Bodycontainer = () => {
  const[listofrest,setlistofrest]=useState([]);
  const[searchtext,setsearchtext]=useState("");
  const[filteredrest,setfilteredrest]=useState([])
  
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const data = await fetch(
      'https://www.swiggy.com/dapi/restaurants/list/v5?lat=12.9352403&lng=77.624532&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING'
    );

    const json = await data.json();
    setlistofrest(json?.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants);
   
    setfilteredrest(json?.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants);
  };

  const activestatus=useActivestatus();
  if(activestatus===false){
    return(
      <div>
        <h1>OOPS you lost internet connection !!!!!!</h1>
      </div>

    )
  }

  return listofrest.length===0 ?<Shimmer/>:(
    <div className='flex flex-col items-center'>
       <div className='fillter flex'>
       <div className='m-4 p-4'>
        <input
        className="border border-black rounded-lg text-sm p-1"
        type='text'
        placeholder='Search For Resturant'
        value={searchtext}
        onChange={(e)=>{
          setsearchtext(e.target.value)
        }}
        />
        
        <button
        className="px-4 py-1 m-2 bg-pink-500 rounded-lg "
         onClick={()=>{
          const filteredres=listofrest.filter((res)=>
            res.info.name.toLowerCase().includes(searchtext.toLowerCase())
          );
          setfilteredrest(filteredres);
        }}>
          search 
        </button>
        </div>

        <div className='m-4 p-4 flex items-center '>
        <button className='px-4 py-2 bg-pink-200 rounded-lg border-pink-700'
         onClick={() => {
           const topRated = listofrest.filter(
           (res) => res.info.avgRating > 4.5
         );
           setfilteredrest(topRated); // Update filteredrest instead
         }}
        >
            Top Rated Resturant â˜…
       </button>

        </div>
      </div>

      <div className="flex flex-wrap gap-6 justify-center md:justify-normal items-center pl-6">
      {filteredrest.map((restaurant) => (
        <Link key={restaurant.info.id} to={"/resturant/"+restaurant.info.id}><Cardresto  Data={restaurant} /></Link>
      ))}
    </div>
    </div>
  );
};

export default Bodycontainer;
