import { useState, useEffect } from 'react';
import Cardresto from './Cardresto';
import Shimmer from './Shimmer';
import { Link } from 'react-router';
import useActivestatus from './custom hooks/useActivestatus';

const Bodycontainer = () => {
  const [listofrest, setlistofrest] = useState([]);
  const [searchtext, setsearchtext] = useState("");
  const [filteredrest, setfilteredrest] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const data = await fetch(
      'https://www.swiggy.com/dapi/restaurants/list/v5?lat=12.9352403&lng=77.624532&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING'
    );

    const json = await data.json();
    const restaurants = json?.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants;
    
    setlistofrest(restaurants); // Set all restaurants initially
    setfilteredrest(restaurants); // Set filtered restaurants initially
  };

  const activestatus = useActivestatus();

  if (activestatus === false) {
    return (
      <div>
        <h1>OOPS you lost internet connection !!!!!!</h1>
      </div>
    );
  }

  return listofrest.length === 0 ? <Shimmer /> : (
    <div className='flex flex-col items-center'>
      <div className='filter flex'>
        <div className='m-4 p-4'>
          <input
            className="border border-black"
            type='text'
            placeholder='Search For Restaurant'
            value={searchtext}
            onChange={(e) => {
              setsearchtext(e.target.value);
            }}
          />
          
          <button
            className="px-4 py-1 m-2 bg-orange-500 rounded-lg"
            onClick={() => {
              const filteredres = listofrest.filter((res) =>
                res.info.name.toLowerCase().includes(searchtext.toLowerCase())
              );
              setfilteredrest(filteredres);
            }}
          >
            Search
          </button>
        </div>

        <div className='m-4 p-4 flex items-center'>
          <button
            className='px-4 py-2 bg-orange-200 rounded-lg'
            onClick={() => {
              const filteredlist = listofrest.filter((res) => res.info.avgRating > 4);
              setfilteredrest(filteredlist); // Update filteredrest, not listofrest
            }}
          >
            Top Rated Restaurant
          </button>
        </div>
      </div>

      <div className="flex flex-wrap justify-between">
        {filteredrest.map((restaurant) => (
          <Link key={restaurant.info.id} to={"/restaurant/" + restaurant.info.id}>
            <Cardresto Data={restaurant} />
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Bodycontainer;
